import React, { useState } from 'react';
import './App.css';
import senfioLogo from './Senfio-White.png'; 

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const emailRegex = /^[a-zA-Z0-9._-]+@senfio\.com$/;
    if (!emailRegex.test(email)) {
      setError('O email deve estar no formato nome.sobrenome@senfio.com');
      return;
    }
    setError('');
  };
  
  return (
    <div className="App" id="box">
      <h1>LOGIN</h1>
      <img src={senfioLogo} id="imagem_logo" alt="Senfio"/>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Email" value={email} onChange={handleEmailChange} />
        <input type="password" placeholder="Password" value={password} onChange={handlePasswordChange} />
        <button type="submit">Login</button>
        {error && <p className="error" style={color="white"}>{error}</p>}
      </form>
    </div>
  );
}

export default App;
