import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    // Defina os campos do formulário e seus valores iniciais aqui
    // Exemplo:
    // username: '',
    // password: '',
  });

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Faça uma requisição POST para a sua view do Django
      const response = await axios.post('/api/urls.py/', formData);

      // Lide com a resposta, se necessário
      console.log(response.data);
    } catch (error) {
      // Lide com erros de requisição, se houver
      console.error('Erro ao enviar formulário:', error);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Mapeie os campos do formulário aqui */}
        {/* Exemplo: */}
        {/* <input type="text" name="username" value={formData.username} onChange={handleChange} /> */}
        {/* <input type="password" name="password" value={formData.password} onChange={handleChange} /> */}
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
}

export default App;
